const batches = $input.all();
const extractedJobs = [];

for (const batch of batches) {
  const job = batch.json.data;

  if (job && job.id) {
    extractedJobs.push({
      json: {
        // Core info
        id: job.id,
        title: job.title,
        description: job.description,
        job_url: job.job_url,
        listed_at: job.listed_at,

        // Location & work mode
        location: job.location,
        country_code: job.country_code,
        work_remote_allowed: job.work_remote_allowed,
        workplace_types: job.workplace_types,

        // Job details
        level: job.level,
        employment_status: job.employment_status,
        salary: job.salary,
        industries: job.industries,
        job_functions: job.job_functions,

        // Company (flattened)
        company_name: job.company?.name,
        company_description: job.company?.description,
        company_size: job.company?.staff_count,
        company_industries: job.company?.industries,
        company_url: job.company?.url
      }
    });
  }
