// Get the current date and time from the special variable $now
const now = new Date($now);

// Modify the date by subtracting 14 days
now.setDate(now.getDate() - 14);

// Convert the modified date to an ISO string
const twoWeeksAgoIso = now.toISOString().split('T')[0];

// The Code node expects to return an array of objects.
// We'll return a single item with a 'twoWeeksAgo' property in its JSON.
return [{
  json: {
    twoWeeksAgo: twoWeeksAgoIso
  }
}];

