// 1. Pobieramy ID z zapytania Webhooka (z węzła "Webhook")
// Używamy .first(), żeby dostać się do danych z triggera
const queryId = $('Webhook').first().json.query.job_id;

// 2. Pobieramy wiersze z Google Sheets (to są dane wejściowe do tego węzła)
const allRows = $input.all();

// Debug (opcjonalnie)
// console.log("Szukam ID:", queryId);

if (!queryId) {
    return { json: { found: false, error: "Brak parametru job_id" } };
}

// 3. Szukamy pasującego wiersza (porównanie stringów, usuwamy spacje)
const matchedRow = allRows.find(item => 
    String(item.json.ID).trim() === String(queryId).trim()
);

if (matchedRow) {
    const data = matchedRow.json;

    // 4. Logika wyboru CV
    // Domyślnie TECH, chyba że znajdziemy co innego
    let cvType = 'TECH';
    const title = (data.Title || '').toLowerCase();

    // --- POPRAWKA: Używamy kolumny 'CV Recommended' ---
    if (data['CV Recommended']) {
        cvType = data['CV Recommended']; 
    } 
    // Fallback: Jeśli kolumna jest pusta, sprawdzamy tytuł stanowiska
    else if (title.includes('manager') || title.includes('head') || title.includes('director') || title.includes('lead') || title.includes('owner')) {
        cvType = 'MANAGER';
    }

    // 5. Budujemy czysty JSON dla Tampermonkey
    return {
        json: {
            found: true,
            id: data.ID,
            score: data.Score,
            cv_type: cvType,
            cover_letter: data['Cover letter'] || "Brak danych.",
            // Mapujemy kolumnę 'Notes' z arkusza na pole 'interview_brief'
            interview_brief: data['Notes'] || "Brak danych.",
            status: data.Status || "new"
        }
    };
} else {
    // Nie znaleziono wiersza
    return {
        json: {
            found: false
        }
    };
}

